(function() {

  require('../src/zappa')(function() {
    this.register({
      jade: this.zappa.adapter('jade')
    });
    this.get({
      '/': function() {
        return this.render({
          'index.jade': {
            foo: 'Zappa + Jade'
          }
        });
      }
    });
    return this.get({
      '/coffeekup': function() {
        return this.render({
          'index.coffee': {
            foo: 'Zappa + CoffeeKup'
          }
        });
      }
    });
  });

}).call(this);
